mapper.select:
select id,
       start_user,
       jumper_port,
       jumper_position,
       link_one,
       link_two,
       local_port,
       open_path,
       channel_code,
       device_port,
       end_user,
       full_routing,
       remarks
from transmission


         and id = #{id}


         and start_user = #{start_user}


         and jumper_port = #{jumper_port}


         and jumper_position = #{jumper_position}


         and link_one = #{link_one}


         and link_two = #{link_two}


         and local_port = #{local_port}


         and open_path = #{open_path}


         and channel_code = #{channel_code}


         and device_port = #{device_port}


         and end_user = #{end_user}


         and full_routing = #{full_routing}


         and remarks = #{remarks}
